import React, { useState } from 'react';
import Person from './components/Persons';

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Martti Tienari', number: '040-123456' },
    { name: 'Arto Järvinen', number: '040-123456' },
    { name: 'Lea Kutvonen', number: '040-123456' }
  ]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [newfilter, setNewFilter] = useState('');

  const nameChange = event => {setNewName(event.target.value)};

  const numberChange = event => {setNewNumber(event.target.value)};

  const filterChange = event => {setNewFilter(event.target.value)};

  const addPerson = (event) => {
    event.preventDefault();
    const name = newName;
    const number = newNumber;
    const personObject = {
      name,
      number
    };

    persons.map(person => person.name).includes(name) ? window.alert(`${name} on jo luettelosssa`)
      : setPersons(persons.concat(personObject));
  };

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <form onSubmit={addPerson} name={newName}>
        <div>
          rajaa näytettäviä:<input value={newfilter} onChange={filterChange} />
        </div>
        <h1>lisää uusi</h1>
        <div>
          nimi:<input value={newName} onChange={nameChange} />
        </div>
        <div>
          numero:<input value={newNumber} onChange={numberChange} />
        </div>
        <div>
          <button type="submit">lisää</button>
        </div>
      </form>
      <h2>Numerot</h2>
      <ul>
        <Person persons={persons} />
      </ul>
      Etsityt:

    </div>
  )
};

export default App;
