import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Country from './components/Country';
import Countries from './components/Countries';
import Filter from './components/Filter';

const App = () => {
  const [countries, setCountries] = useState([]);
  const [filter, setFilter] = useState('');
  const [filteredCountries, setFilteredCountries] = useState(countries);

  const countriesUrl = 'https://restcountries.eu/rest/v2/all';

  useEffect(() => {
    axios
      .get(countriesUrl)
      .then(response => {
        setCountries(response.data);
        setFilteredCountries(response.data);
        console.log(response.data);
      })
  }, [])

  const handleFilter = (event) => {
    setFilter(event.target.value);
    setFilteredCountries(
      countries.filter(country => country.name.toLowerCase().includes(event.target.value.toLowerCase()))
    );
  };

  const handleReturn = () => {
    if (filteredCountries.length > 10) {
        return <p>too many results, try narrowing your search</p>
    } else if (filteredCountries.length === 1) {
        return <Country country={ filteredCountries[0] } />
    } else {
        return <Countries countries={ filteredCountries } setFilter={ setFilter } />
    }
}

return (
    <div>
        <Filter filter={filter} set={handleFilter} />
        { handleReturn() }
    </div>
)
};

ReactDOM.render(<App />, document.getElementById('root'));
