import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Person from './components/Persons';
import Filter from './components/Filter';
import AddForm from './components/AddForm';

const App = () => {
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [newFilter, setNewFilter] = useState('');
  const [filteredList, setNewFilteredList] = useState(persons);

  const hook = () => {
    // console.log('effect')
    axios
      // .get('http://localhost:3001/persons')
      // .get('https://restcountries.eu/rest/v2/name/finland')
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        // console.log('promise fulfilled')
        setPersons(json(response.data))
      })
  };
  useEffect(hook, []);
  console.log('',persons);

  const nameChange = event => { setNewName(event.target.value) };
  const numberChange = event => { setNewNumber(event.target.value) };

  const handleFilter = (event) => {
    setNewFilteredList(
      persons.filter(person =>
        person.name.toLowerCase().includes(newFilter.toLowerCase())
      )
    );
    setNewFilter(event.target.value);
  };

  const addPerson = (event) => {
    event.preventDefault();
    const name = newName;
    const number = newNumber;
    const personObject = {
      name, number
    };

    persons.map(person => person.name).includes(name) ? window.alert(`${name} on jo luettelosssa`)
      : setPersons(persons.concat(personObject));
  };

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <Filter newFilter={newFilter} handleFilter={handleFilter} />
      <AddForm
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        nameChange={nameChange}
        numberChange={numberChange}
      />
      <h1>Numerot</h1>
      <Person persons={filteredList} />
    </div>
  )
};

export default App;
