import React from 'react'

const Filter = ({ setFilter, filter, handleFilter }) => {
  return (
      <div>
        search: <input onChange={(e) => 
          setFilter(e.target.value)
        .then(response => {
          handleFilter()
        })} value={filter} />
      </div>
  )
}

export default Filter
