import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Weather = ({ capital }) => {
  // const APIKEY = '756afc82d0004ff0a3992626191504&q';
  const APIKEY = process.env.API_KEY_APIXU;
  const [weather, setNewWeather] = useState(
    {
      current: {
        temp_c: "n/a",
        wind_kph: "n/a",
        wind_dir: "n/a",
        condition: {
          icon: "#"
        }
      }
    }
  );

  console.log('apikey', api)
  const weatherApi = 'http://api.apixu.com/v1/current.json?key';
  const apiKey = '756afc82d0004ff0a3992626191504&q';
  // const weather = `${weatherApi}=${apiKey}=${capital}`;

  console.log('weather in:', capital)

  useEffect(() => {
    axios
      .get(`https://api.apixu.com/v1/current.json?key=${APIKEY}&q=${capital}`)
      .then(response => {
        setNewWeather(response.data);
      });
  }, []);

  console.log(weather.current);
  console.log(weather);

  return (
    <div>
      <h1>Wheather in {capital}</h1>
      <p>temperature: {weather}</p>
      <p>wind: </p>
    </div>
  )

}

export default Weather;



