import React, { useState, useEffect } from "react";
import axios from "axios";

const Weather = ({ capital }) => {
  const API_KEY2 = process.env.REACT_APP_SECRET_CODE;
  const API_KEY = '756afc82d0004ff0a3992626191504&q';
  const [weather, setNewWeather] = useState({});

  useEffect(() => {
    axios
      .get(`https://api.apixu.com/v1/current.json?key=${API_KEY}&q=${capital}`)
      .then(response => {
        setNewWeather(response.data);
      });
  }, []);

  console.log(weather.current);
  console.log(weather);
  console.log('api_key2', API_KEY2);
  console.log('REACT_APP', process.env.REACT_APP_SECRET_CODE)

  return (
    <div>
      <h3>Weather in the capital {capital}</h3>
      <p>
        <strong>Temperature:</strong> {weather.current.temp_c} Celsius
      </p>
      <img src={weather.current.condition.icon} alt="" width="100px" />
      <p>
        <strong>Wind:</strong> {weather.current.wind_kph} kph to {weather.current.wind_dir}
      </p>
    </div>
  );
};

export default Weather;
