import React from 'react';

const Countries = ({ countries, setFilter, setFilteredCountries }) =>

  (
    <div>
      {countries.map(country => (
        <div key={country.numericCode}>
          {country.name}
          <ShowCountryButton
            country={country}
            setFilter={setFilter}
            setFilteredCountries={setFilteredCountries}
          />
        </div>
      ))}
    </div>

  )


const ShowCountryButton = ({ country, setNewFilter, setFilteredCountries }) => {
  const showCountry = () => {
    setNewFilter(country.name);
    setNewFilteredCountries([country]);
  };
  return <button onClick={showCountry}>show</button>;
};

export default Countries;
