import React, { useEffect } from 'react';
import axios from 'axios';

const Country = ({ country, useEffect }) => {

  (
    <div>
      <h1>{country.name}</h1>
      <div>Capital: {country.capital}</div>
      <div>Population: {country.population}</div>
      <h2>Languages</h2>
      <ul>
        {country.languages.map(language => (
          <li key={language.iso639_1}>{language.name}</li>
        ))}
      </ul>
      <img src={country.flag} width = '100'/>
    </div>
  )

  // const weather  = `http://api.apixu.com/v1/current.json?key=756afc82d0004ff0a3992626191504&q=${location}`;

  useEffect(() => {
    axios
      .get(`http://api.apixu.com/v1/current.json?key=756afc82d0004ff0a3992626191504&q=${location}`)
      .then(response => {
        console.log(response.data);
      })
  }, [])

}



export default Country;
