import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Weather = ({ country, capital }) => {
  const [weatherState, setWeather] = useState([]);

  const weatherApi = 'http://api.apixu.com/v1/current.json?key';
  const apiKey = '756afc82d0004ff0a3992626191504&q';
  const weather = `${weatherApi}=${apiKey}=${capital}`;

  console.log('from weather', capital)

  useEffect(() => {
    axios
      .get(weather)
      .then(response => {
        // setWeather(response.data);
        console.log(response.data);
      })
  }, [])

  return (
    <div>
    <h1>Wheather in {capital}</h1>
    <p>temperature: {weatherState}</p>
    <p>wind: </p>
    </div>
  )

}


export default Weather;
