import React, { useState, useEffect} from 'react';
import axios from 'axios';

const Weather = ({ country }) => {

  const weatherApi = 'http://api.apixu.com/v1/current.json?key';
  const apiKey = '756afc82d0004ff0a3992626191504&q';
  const location = 'helsinki';
  const weather = `${weatherApi}=${apiKey}=${location}`;
    // const weather  = `http://api.apixu.com/v1/current.json?key=756afc82d0004ff0a3992626191504&q=${location}`;


  useEffect(() => {
    axios
      .get(weather)
      .then(response => {
        console.log(response.data);
      })
  }, [])
}


export default Weather;
