import React, { useState, useEffect } from 'react';
import Persons from './components/Persons';
import Filter from './components/Filter';
import AddForm from './components/AddForm';
import personService from './services/persons';

const App = () => {
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [newFilter, setNewFilter] = useState('');
  const [filteredList, setFilteredList] = useState(persons);
  const [ notification, setNotification ] = useState(null)

  useEffect(() => {
    personService.getAll()
      .then(initialPersons => {
        setPersons(initialPersons);
        setFilteredList(initialPersons);
      });
  }, []);

  useEffect(() => {
    console.log('', persons);
  }, [persons])

  const nameChange = event => { setNewName(event.target.value) };
  const numberChange = event => { setNewNumber(event.target.value) };

  const handleFilter = event => {
    setNewFilter(event.target.value);
    setFilteredList(
      persons.filter(person => person.name.toLowerCase().includes(event.target.value.toLowerCase())
      )
    );
  };

  const addPerson = (event) => {
    event.preventDefault();
    const name = newName;
    const number = newNumber;
    // const newObject = { name, number}

    if (persons.map(person => person.name.toLowerCase()).includes(name.toLocaleLowerCase())) {
      if (window.confirm(`${name} on jo luettelosssa, korvataanko vanha numero uudella?`)) {
        console.log('korvataan')
        let personToReplaced = persons.filter(a => a.name = newName)[0]
        personService
          .update(personToReplaced.id, newObject = { name, number })
          .then(res => {
            setPersons(persons.map(person => (person.id === personToReplaced.id) ? res : person))
            setNotification(`Kontaktin ${newName} numero päivitetty.`)
            setTimeout(() => { setNotification(null) }, 2500);
          })
      }
      setNewName('')
      setNewNumber('')
    } else {
      const personObject = { name, number };
      personService
        .create(personObject).then(returnedPerson => {
          setFilteredList(persons.concat(returnedPerson));
          setPersons(persons.concat(returnedPerson));
          // console.log('-->', filteredList)
          setNewName('')
          setNewNumber('')
        })
    }
  };

  const removePerson = ({ id, name }) => {
    if (window.confirm(`Poistetaanko ${name} luettelosta`)) {
      personService.remove(id).catch(error => {
        alert(`${id.nimi} ei löydy kannasta`)
      });
      setFilteredList(filteredList.filter(person => person.id !== id))
    }
  };

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <Filter newFilter={newFilter} handleFilter={handleFilter} />
      <AddForm
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        nameChange={nameChange}
        numberChange={numberChange}
      />
      <h1>Numerot</h1>
      <Persons persons={filteredList} removePerson={removePerson} />
    </div>
  )
};

export default App;
