import React, { useState, useEffect } from 'react';
import Persons from './components/Persons';
import Filter from './components/Filter';
import AddForm from './components/AddForm';
import personService from './services/persons';

const App = () => {
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [newFilter, setNewFilter] = useState('');
  const [filteredList, setNewFilteredList] = useState(persons);

  useEffect(() => {
    personService.getAll().then(initialPersons => {
      setPersons(initialPersons);
      setNewFilteredList(initialPersons);
    });
  }, []);

  useEffect(() => {
    console.log('', persons);
  }, [persons])

  const nameChange = event => { setNewName(event.target.value) };
  const numberChange = event => { setNewNumber(event.target.value) };

  const handleFilter = event => {
    setNewFilter(event.target.value);
    setNewFilteredList(
      persons.filter(person =>
        person.name.toUpperCase().includes(event.target.value.toUpperCase())
      )
    );
  };

  const addPerson = (event) => {
    event.preventDefault();
    const name = newName;
    const number = newNumber;

    if (persons.map(person => person.name).includes(name)) {
      window.alert(`${name} on jo luettelosssa`)
    } else {
      const personObject = { name, number };
      personService
        .create(personObject)
        .then(returnedPerson => {
          setNewFilteredList(persons.concat(returnedPerson));
          setNewName('')
          setNewNumber('')
        })
    }
  };

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <Filter newFilter={newFilter} handleFilter={handleFilter} />
      <AddForm
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        nameChange={nameChange}
        numberChange={numberChange}
      />
      <h1>Numerot</h1>
      <Persons persons={filteredList} />
    </div>
  )
};

export default App;
